sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   // Clear the choices first
    g_form.clearOptions('u_what_needed');

    // Check if it's not a new record
    if (!g_form.isNewRecord()) {
        // Store the previous value of What Needed
        var previousValue = g_form.getValue('u_what_needed');

        // Re-populate the What Needed field based on the Request Type
        var requestType = g_form.getValue('u_request_type');

        if (requestType === 'hr') {
            g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
            g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
            g_form.addOption('u_what_needed', 'other', 'Other');
        } else if (requestType === 'facilities') {
            g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
            g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
            g_form.addOption('u_what_needed', 'other', 'Other');
        } else if (requestType === 'legal') {
            g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
            g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
            g_form.addOption('u_what_needed', 'other', 'Other');
        }

        // Set the previous value back in the What Needed field
        g_form.setValue('what_needed', previousValue);
    }

}
